<template>
  <div>
    <div class="text-center">
      <div class="my-3">
        <h1>Add Devices</h1>

        <br />

        <button @click="toggleForm" type="submit" class="btn btn-primary">
          Toggle Form
        </button>
        <br />
        <small>Press the button to register your devices</small>
      </div>
    </div>

    <div class="container center-div" v-if="formToggle">
      <form>
        <fieldset>
          <legend>Register Device</legend>
          <div class="form-group">
            <label for="deviceTypeInput">Device Type</label>
            <input
              v-model="deviceType"
              type="text"
              class="form-control"
              id="deviceTypeInput"
              aria-describedby="deviceTypeHelp"
              placeholder="Enter Type"
              style='background-image: url("data:image/png;base64,"); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: auto;'
            />
            <small id="deviceTypeHelp" class="form-text text-muted"
              >Desktop, Laptop, Server, Printer, etc.</small
            >
          </div>
          <div class="form-group">
            <label for="DescriptionInput">Description</label>
            <input
              v-model="description"
              type="text"
              class="form-control"
              id="DescriptionInput"
              placeholder="Enter Description"
              style='background-image: url("data:image/png;base64,"); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;'
            />
          </div>
          <div class="form-group">
            <label for="IPInput">IP Address</label>
            <input
              v-model="ip"
              type="text"
              class="form-control"
              id="IPInput"
              placeholder="Enter IP"
              style='background-image: url("data:image/png;base64,"); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;'
            />
          </div>
        </fieldset>
        <button @click="submitDevice" type="submit" class="btn btn-primary">
          Submit
        </button>
        <div v-show="device_show" style="max-width: 30%">
          <br />
          <div class="alert alert-dismissible alert-success">
            <button
              @click="closeDeviceShow"
              type="button"
              class="close"
              data-dismiss="alert"
            >
              &times;
            </button>
            <strong>Device Submitted Successfully!</strong>
          </div>
        </div>
      </form>
    </div>

    <br />
    <br />
    <br />
    <br />

    <h1 class="text-center">Registered Devices</h1>

    <br />
    <br />

    <div class="container">
      <div v-for="(group, index) in groupAlerts" :key="index" class="row">
        <div v-for="device in group" :key="device._id" class="col">
          <DeviceInfo
            :deviceType="device.deviceType"
            :description="device.description"
            :ip="device.ip"
            :mongoID="device._id"
          />
          <br />
        </div>
      </div>
    </div>
  </div>
</template>

<script>
const API_URL_DEVICES = "api/devices";
import DeviceInfo from "@/components/DeviceInfo.vue";

export default {
  name: "devices",
  data: function() {
    return {
      formToggle: false,
      deviceType: "",
      description: "",
      ip: "",
      deviceList: [],
      device_show: false
    };
  },
  mounted() {
    fetch(API_URL_DEVICES)
      .then(response => response.json())
      .then(result => {
        this.deviceList = result;
      });
  },
  computed: {
    groupAlerts() {
      let index = 0;
      let arrayLength = this.deviceList.length;
      let tempArray = [];
      let myChunk = null;
      let chunk_size = 3;
      let array = [...this.deviceList];

      if (arrayLength > 0) {
        for (index = 0; index < arrayLength; index += chunk_size) {
          myChunk = array.slice(index, index + chunk_size);
          tempArray.push(myChunk);
        }

        return tempArray;
      }
      return [];
    }
  },
  methods: {
    submitDevice() {
      this.device_show = false;

      var send = {
        deviceType: this.deviceType,
        description: this.description,
        ip: this.ip
      };

      fetch(API_URL_DEVICES, {
        method: "POST",
        body: JSON.stringify(send),
        headers: {
          "content-type": "application/json"
        }
      });

      this.device_show = true;
      this.deviceType = "";
      this.description = "";
      this.ip = "";
    },
    toggleForm() {
      this.formToggle = !this.formToggle;
    },
    closeDeviceShow() {
      this.device_show = false;
    }
  },
  components: {
    DeviceInfo
  }
};
</script>

<style>
.col {
  max-width: 30%;
}
</style>
