<template>
  <div class="text-center">
    <h1 class="my-3">IDS Module</h1>

    <p>
      Longer Description for the IDS Module Here. If you would like to add a
      known machine please fill out the form below.
    </p>

    <div class="container center-div">
      <form>
        <fieldset>
          <legend>Network Map Form</legend>
          <div class="form-group">
            <label for="deviceTypeInput">Device Type</label>
            <input
              v-model="deviceType"
              type="text"
              class="form-control"
              id="deviceTypeInput"
              aria-describedby="deviceTypeHeelp"
              placeholder="Enter Type"
              style='background-image: url("data:image/png;base64,"); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: auto;'
            />
            <small id="deviceTypeHelp" class="form-text text-muted"
              >Desktop, Laptop, Server, Printer, etc.</small
            >
          </div>
          <div class="form-group">
            <label for="DescriptionInput">Description</label>
            <input
              v-model="description"
              type="text"
              class="form-control"
              id="DescriptionInput"
              placeholder="Enter Description"
              style='background-image: url("data:image/png;base64,"); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;'
            />
          </div>
          <div class="form-group">
            <label for="exampleInputPassword1">IP Address</label>
            <input
              v-model="ip"
              type="text"
              class="form-control"
              id="exampleInputPassword1"
              placeholder="Enter IP"
              style='background-image: url("data:image/png;base64,"); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;'
            />
          </div>
        </fieldset>
        <button @click="submitDevice" type="submit" class="btn btn-primary">
          Submit
        </button>
      </form>
    </div>

    <h2 class="my-5">Alerts</h2>

    <div class="container">
      <div v-for="(group, index) in groupAlerts" :key="index" class="row">
        <div v-for="alert in group" :key="alert._id" class="col">
          <AlertInfo
            :modID="alert.modID"
            :description="alert.description"
            :severity="alert.severity"
          />
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import AlertInfo from "@/components/AlertInfo.vue";
const API_URL_ALERTS = "../api/alerts";
const API_URL_DEVICES = "../api/devices";

export default {
  name: "ids",
  data: function() {
    return {
      alertList: [],
      deviceType: "",
      description: "",
      ip: ""
    };
  },
  mounted() {
    fetch(API_URL_ALERTS)
      .then(response => response.json())
      .then(result => {
        this.alertList = result;
      });
  },
  methods: {
    submitDevice() {
      var send = {
        deviceType: this.deviceType,
        description: this.description,
        ip: this.ip
      };

      fetch(API_URL_DEVICES, {
        method: "POST",
        body: JSON.stringify(send),
        headers: {
          "content-type": "application/json"
        }
      });

      this.deviceType = "";
      this.description = "";
      this.ip = "";
    }
  },
  computed: {
    groupAlerts() {
      let index = 0;
      let arrayLength = this.alertList.length;
      let tempArray = [];
      let myChunk = null;
      let chunk_size = 3;
      let array = [...this.alertList];

      if (arrayLength > 0) {
        for (index = 0; index < arrayLength; index += chunk_size) {
          myChunk = array.slice(index, index + chunk_size);
          tempArray.push(myChunk);
        }

        return tempArray;
      }
      return [];
    }
  },
  components: {
    AlertInfo
  }
};
</script>

<style>
.col {
  max-width: 30%;
}
</style>
